import React ,{useState , useEffect} from 'react'

function SearchAnime(props){
    
    const SEARCH_API = `https://kitsu.io/api/edge/anime?filter[text]=$`

    const style={
        fontSize: "1.7rem", 
        textAlign: "center" ,
        width:"500px" ,
        borderRadius:"25px",
        maxWidth:"100%",

    }
        

    return(
        <div style={{textAlign: "center"}}>     
<input style={style} type="text" name="name" placeholder="Search..." onChange={props.change} />
        </div>
    )   
        
    
}


export default SearchAnime
