import React from 'react'

import Slider from 'react-slick'

function TrendingCard(props){


    console.log(props.data)

    var settings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 4,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2,
              initialSlide: 2
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
      }


    

    return(
      


 props.data.map((e)=>{
   return <Slider {...settings}>
    <div>
      <h3>1</h3>
    </div>
  </Slider>
     
        return <div key={e.id}>
            <h1>{e.attributes.canonicalTitle}</h1>
            <img alt={e.attributes.canonicalTitle} src={e.attributes.posterImage.small}></img>
        </div>
    })
    
       )
}

export default TrendingCard
