import React,{useState, useEffect} from 'react'
import Card from './cards'
import SearchAnime from './SearchAnime'
import SingelAnime from './SingelAnime'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams,
    useRouteMatch
  } from "react-router-dom";


function Movies(){

const [data , setData] = useState([])
const [isLoading , setIsLoading] = useState(true)
const [value , SetValue] = useState()
const [isBottom, setIsBottom] = useState(true);
const [page , setPage] = useState(0)
const [show, setShow] = useState(false)
const [num , setNum]= useState(1)




const LISTED_API= `https://kitsu.io/api/edge/anime?page[limit]=10&page[offset]=${page}`
const SEARCH_API = `https://kitsu.io/api/edge/anime?filter[text]=$`


useEffect(() => {
    if (isBottom) { 
        if(value){
            return
        }else
        setPage((prev)=>prev + 20)
        readData(LISTED_API)
    }
  }, [isBottom]); 

//FEATCHING API DATA , SET DATA , Set Loading //

const readData = (API)=> {
    setIsLoading(true)
    fetch(API)
     .then((res)=>res.json())
     .then((data)=>{
         if(!value){
             if(page === 0){
                setData(prev => [...data.data])
             }if(page > 0){
                setData(prev => [...prev, ...data.data])         
             }     
         }else {
             setData((prev)=>[...data.data])
         }
 
       })
     .then(setIsLoading(false))
   
 }


function HideItemId(e){
    setNum(e.target.value)
    setShow(true)  
    
}

function ShownItemId(){
    return setShow(false) 

}


//search handel//

function handelChange(e){
    const searchValue = e.target.value
        SetValue( searchValue)
        readData(SEARCH_API+searchValue)

        if (!searchValue){
        SetValue(null)
        setData([])
        setPage((prev)=>prev  * 0)
        readData(LISTED_API) 
        }

}
//Listen to Scroll //

useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);


//Set True if reach Buttom//

function handleScroll(){
        if ((window.innerHeight + window.scrollY) >= document.body.offsetHeight-100) {
            setIsBottom(true)
         }else{
             setIsBottom(false)
         }
}

//close singel if ESC got clicked

useEffect(() => {
    const close = (e) => {
      if(e.keyCode === 27){
       ShownItemId()
      }
    }
    window.addEventListener('keydown', close)
  return () => window.removeEventListener('keydown', close)
},[])

  









function Topics() {
    // The `path` lets us build <Route> paths that are
    // relative to the parent route, while the `url` lets
    // us build relative links.
    let { path, url } = useRouteMatch();
  
    return (
      <div>
        <h2>Topics</h2>
        <ul>
          <li>
          <Link to="/" component= {show ? <SingelAnime cardNumber={num} showOn={ShownItemId} /> : null } />
          </li>
          <li>
            <Link to={`${url}/components`}>Components</Link>
          </li>
          <li>
            <Link to={`${url}/props-v-state`}>Props v. State</Link>
          </li>
        </ul>
  
        <Switch>
          <Route exact path={path}>
            <h3>Please select a topic.</h3>
          </Route>
          <Route path={`${path}/:topicId`}>
         
          </Route>
        </Switch>
      </div>
    );
  }


  function Home() {
    return (
      <div>
        <h2>Home</h2>
      </div>
    );
  }


  function Topic() {
    // The <Route> that rendered this component has a
    // path of `/topics/:topicId`. The `:topicId` portion
    // of the URL indicates a placeholder that we can
    // get from `useParams()`.
    let { topicId } = useParams();
  
    return (
      <div>
        <h3>{topicId}</h3>
      </div>
    );
  }





return(<div>

<header>
   
 <SearchAnime change={(e)=>{handelChange(e)}}/>

</header> 


{show ? <SingelAnime cardNumber={num} showOn={ShownItemId} /> : null }


<Router>
      <div>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/topics">Topics</Link>
          </li>
        </ul>

        <hr />

        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/topics">
            <Topics />
          </Route>
        </Switch>
      </div>
    </Router>

<div className="grid-container">

{isLoading ? 'Loading...' : data.map(e=>
      <Card 
      key={e.id}
     id={e.id}
    name={e.attributes.canonicalTitle}
    img={e.attributes.posterImage.small}
    description={e.attributes.description}
    showOn={ShownItemId} 
    showOff={HideItemId} 

    />)}

</div>


</div>)
}
    export default Movies
