import React , {useState , useEffect} from 'react'
import Skeleton, { SkeletonTheme } from 'react-loading-skeleton'


function Character(props){

    

    const [Character , SetCharacter] = useState([])
    const SINGEL_CHARACTER_API = `https://kitsu.io/api/edge/media-characters/${props.id}/character`



    useEffect(()=>{ 

        fetch(SINGEL_CHARACTER_API)
        .then((res)=>res.json())
        .then((data)=>{
            SetCharacter(prev=> ({ ...data.data,...prev.data}))
            }) 
        }
        ,[])

        
let img_url

    return(
    <div>  
         {Character.id ? 
        <div>
        { !Character.attributes.image ?
         img_url = "https://pm1.narvii.com/6529/68caee022fc10f36230f46eff8e6e4386f5e11e7_hq.jpg" : 
         img_url = Character.attributes.image.original}

        </div>
         : null }
    </div>
    )
}

export default Character
