export const getData = async page=>{
    const anime = await (
        await fetch(`https://kitsu.io/api/edge/anime?page[limit]=20&page[offset]=${page}`)
    ).json()
    return anime.data
}


export const searchData = async text=>{
    const anime = await (
        await fetch(`https://kitsu.io/api/edge/anime?filter[text]=${text}`)
    ).json()
    return anime.data
}
