import React from 'react'
import Slider from 'react-slick'



function TrendingCard(props){



    console.log(props.data)   


    var settings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 5,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
              initialSlide: 1
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
      };


     
    return(
        <div >
        <Slider {...settings}>
 {props.data.map((e)=>{
     
        return <div  key={e.id}>
        <div style={{backgroundImage: "url(" + e.attributes.posterImage.small + ")" , position: "relative" ,
        height:"450px",
        backgroundSize:"cover", 
        margin : "0% 10px",
        backgroundRepeat:"no-repeat",}}>
        <h1  style={{backgroundColor:"#000000c4" , color:"white" , textAlign:"center" , margin:"0% auto" , padding:"5% 0%"}} >{e.attributes.canonicalTitle}</h1>
        </div>
       
        </div>
    })}
    </Slider>
        </div>
       )
}

export default TrendingCard
