import React, { useState, useEffect } from "react";
import Card from "./cards";

const Fetch = () => {
  const [data, setData] = useState(null);
  const [isLoading , setIsLoading] = useState(true)

  const style={
    display:"grid" ,
    gridTemplateColumns: "auto auto auto auto"
}

  useEffect(()=>{
    setTimeout(()=>fetch('https://kitsu.io/api/edge/trending/anime')
      .then(response => response.json())
      .then(json => {        
          setData(json)
        setIsLoading(false)        
      }),1000)
  
  },[])

  console.log(data)
  return(
  isLoading ? 'Loading...' : data.data.map(e=><div style={style} key={e.id}>
    <Card id={e.id} name={e.attributes.canonicalTitle} img={e.attributes.posterImage.small} description={e.attributes.description}/>
   
      </div>)
     
  )};

export default Fetch;
