import React,{useState, useEffect} from 'react'
import Card from './cards'
import Search from './SearchAnime'


function Movies(){

const [data , setData] = useState([])
const [isLoading , setIsLoading] = useState(true)
const [searchTerms , setSerchTerms]= useState(false)
const [value , SetValue] = useState('')

const style={
    fontSize: "2rem", 
    textAlign: "center" ,
    borderRadius:"25px",
    padding : "0% 20%" ,
    marginLeft:"19%"
}


const LISTED_API= "https://kitsu.io/api/edge/trending/anime"
const SEARCH_API = `https://kitsu.io/api/edge/anime?filter[text]=${value}`




useEffect(()=>{
 
        readData(LISTED_API)
    

},[])


function handelChange(e){
    setSerchTerms(true)
    SetValue(e.target.value)  
    readData(SEARCH_API)
    if(value = ''){
        setSerchTerms(false)
    }else return

}

const readData = ((API)=>{
   fetch(API)
    .then((res)=>res.json())
    .then((data)=>{
        setData(data.data)
        setIsLoading(false)
    })
})



console.log(data)
return(<div>

<header>

   <input style={style} type="text" name="name" placeholder="Search..." onChange={handelChange} />

</header>

<div className="grid-container">
{isLoading ? 'Loading...' : data.map(e=>
      <Card 
      key={e.id}
     id={e.id}
    name={e.attributes.canonicalTitle}
    img={e.attributes.posterImage.small}
    description={e.attributes.description}/>
     
        
       
    )};

</div>


</div>)
}
    export default Movies
