import React, { useState, useEffect } from "react";
import Card from "./cards";

const Fetch = () => {
  const [data, setData] = useState(null);
  const [isLoading , setIsLoading] = useState(true)
  const gridContainer={
    display: "grid",
  gridTemplateColumns: "auto auto auto",
  backgroundColor:"#2196F3",
  padding: "10px", 
}

  useEffect(()=>{
    setTimeout(()=>fetch('https://kitsu.io/api/edge/trending/anime')
      .then(response => response.json())
      .then(json => {        
          setData(json)
        setIsLoading(false)        
      }),1000)
  
  },[])

  console.log(data)
  return(
  isLoading ? 'Loading...' : data.data.map(e=>
    <Card id={e.id} name={e.attributes.canonicalTitle} img={e.attributes.posterImage.small} description={e.attributes.description} style={gridContainer}/>
   
      )
     
  )};

export default Fetch;
