import React , {useState , useEffect} from 'react'

function ActorCard(props){

    

    const [Actor , SetActor] = useState([])
    const SINGEL_ACTOR_API = `https://kitsu.io/api/edge/media-staff/${props.id}/person`


    useEffect(()=>{ 

        fetch(SINGEL_ACTOR_API)
        .then((res)=>res.json())
        .then((data)=>{
            SetActor(prev=> ([data.data]))
            }) 
        }
        ,[])



    return(!Actor ? "...Loading": Actor.map((e)=>{
   return <div style={{position:"relative"}} key={e.id}>
        {e.attributes.name}
{e.attributes.image ? <img style={{width:"250px" ,height:"250px"}}src={e.attributes.image.original}/> :<img style={{width:"250px" ,height:"250px"}} src="https://www.kindpng.com/picc/m/52-526798_anonymous-person-transparent-hd-png-download.png"/>}
    </div>
    }) 

    )
}

export default ActorCard
