import React from 'react'
import { useHistory } from 'react-router-dom';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";
import SingelEpisode from './components/Episodes'
import Characters from './components/characters'

function RouterSingelAnime(props){


    function Characterr() {
        return (
          <div>
          
          <h2>Characters</h2>
           <Characters num={props.num}></Characters>
          </div>
        );
      }
      
      function Episode() {
        return (
          <div>
          <h2>Episodes</h2>
            <SingelEpisode num={props.num}></SingelEpisode>
          </div>
        );
      }
      
      function Actors() {
        return (
          <div>
            <h2>Actors</h2>
          </div>
        );
      }
    
    
       const history = useHistory()

console.log(history)

    return <div>
              <Router>
      <div style={{textAlign:"center"}} >
        <ul className="router-Grid">
          <li>
            <Link to={`${history.location+props.num}/Episode`}>Episode</Link>
          </li>
          <li>
            <Link to={`${history.location.pathname}/Characters`}>Character</Link>
          </li>
          <li>
            <Link to={`${history.location.pathname}/Actors`}>Actors</Link>
          </li>
        </ul>

        <hr />
        <Switch>
          <Route exact path={`${history.location+props.num}/Episode`}>
            <Episode/>
          </Route>
          <Route path={`${history.location.pathname}/Characters`}>
            <Characterr/>
          </Route>
          <Route path={`${history.location.pathname}/Actors`}>
            <Actors/>
          </Route>
        </Switch>
      </div>
    </Router>
    </div>
}

export default RouterSingelAnime
