import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { useSpring , animated } from 'react-spring'


import SearchAnime from './SearchAnime'
import HomeSvg from './SVG/HomeSVG'
import MovieSVG from './SVG/MovieSVG'
import SearchSVG from './SVG/SearchSVG'

function NavBar(){

const[show,setShow]=useState(false)

const props = useSpring({
    to: async (next, cancel) => {
      await next({opacity: 1, color: '#ffaaee'})
      await next({opacity: 0, color: 'rgb(14,26,19)'})
    },
    from: {opacity: 0, color: 'red'}
  })

function ShowSearchBar(props){
    console.log(show)   
     return setShow(!show)
}


   return(
       <div > 
      {!show ? <h1>none</h1> : <animated.div style={props}>I will fade in and out</animated.div> }
       <div className="navBar" >
    <div className="navBar-item">
    <Link to="/"><HomeSvg></HomeSvg></Link> 
    <Link to="/movies"><MovieSVG/></Link>
    <div onClick={ ShowSearchBar}>
    <SearchSVG/>
    </div>
    </div>  
    </div>
     </div>)
}
export default NavBar
