import React, { useState, useEffect } from "react";
import useInfiniteScroll from "./useInfinite"

const Anime = () => {
  const [data, setData] = useState([]);
  const [page, setPage] = useState(1);
  const [isFetching, setIsFetching] = useInfiniteScroll(moreData);

  const loadData = () =>{
    let url = "https://kitsu.io/api/edge/anime";
    fetch(url).then(res => {
      setData(res.data);
    });
  }
  function moreData() {
    let url = `https://medrum.herokuapp.com/feeds/?source=5718e53e7a84fb1901e05971&page=${page}&sort=latest`;
    fetch(url).then(res => {
      setData([...data, ...res.data]);
      setPage(page+1)
      setIsFetching(false)
    });
  }
  
  useEffect(()=>{
    loadData()
  }, [])

  console.log(data)

  if (data.length==0) {
    return <h1>Loading...</h1>;
  }

  return (
    <>
      <ul className="list-group-ul">
        {data.map((article, key) => (
          <li className="list-group-li" key={key}>
            <a href={article.url} target="_blank">
              {article.title}
            </a>
          </li>
        ))}
      </ul>
    </>
  );
};

export default Anime
