import React from 'react'
import Slider from 'react-slick'



function TrendingCard(props){



    console.log(props.data)   


    const settings = {
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 3
      }


     
    return(
        <div >
        <Slider {...settings}>
 {props.data.map((e)=>{
     
        return <div  key={e.id}>
        <div style={{backgroundImage: "url(" + e.attributes.posterImage.small + ")" , position: "relative" ,
        height:"450px",
        backgroundSize:"cover", 
        margin : "0% 10px",
        backgroundRepeat:"no-repeat",}}>
        <h1  style={{backgroundColor:"#000000c4" , color:"white" , textAlign:"center" , margin:"0% auto" , padding:"5% 0%"}} >{e.attributes.canonicalTitle}</h1>
        </div>
       
        </div>
    })}
    </Slider>
        </div>
       )
}

export default TrendingCard
