import Characters from './characters'



function Character(props){

    

    const [Character , SetCharacter] = useState([])
    const [Character_ID , setCharacter_ID] = useState([])
    const SINGEL_CHARACTER_API = `https://kitsu.io/api/edge/media-characters/${props.id}/character`

    const readCharacter = (API)=>{
        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            SetCharacter(prev=> ({...prev ,data: [data.data,prev.data]}))
            }) 
        .then(()=>{
            Test()
        })
    }   

    function Test(){
return console.log(Character)
    }

    useEffect(()=>{ 
        readCharacter(SINGEL_CHARACTER_API)
        },[])




    return(
    <div>
         <h1>{props.id}</h1>     
    </div>
    )
}

export default Character
