    return <div className="grid-container" >
            {!Characters.data ? null :Characters.data.map((e)=>{
       return <div  key={e.id}> 
       <h1>{props.id}</h1>    
       {e.id ?   <div> 
 
       </div>   : "Loading"}
   </div>
    })}
    </div>
}

export default Characters
