import React,{useState} from 'react'
import './App.css';
import Movies from './components/AnimeHouse'

import { Route, useParams , Switch, useLocation} from 'react-router-dom';

import SingelAnime from './components/SingelAnime';
import { useTransition, animated  } from 'react-spring'
function App() {

 const SingelAnimel= (props)=>{
  let { id } = useParams();
    return  <SingelAnime cardNumber={id}/>
 }

 const { location } =  useLocation();
  const transitions = useTransition(location, location => location.pathname, {
    initial: { transform: 'translate3d(0, 0%,0)'},
    from: { transform: 'translate3d(0, 80%,0)'},
    enter: { transform: 'translate3d(0, 0%,0)'},
    leave: { transform: 'translate3d(0, -90%,0)'}
  })


  return (
    <div className="dis">


<div className="grid-container">



{transitions.map(({ item, props, key}) => (
      <animated.div 
        style={{...props, position: "absolute", height: "100%", width: "100%"}} 
        key={key}>
        <Switch location={item}>
    <Route path="/" component={Movies}/>
        </Switch>
        <Route exact path="/:id" component={SingelAnimel} />
      </animated.div>
    ))}



    <Router>


    </Router>
</div>

    </div>

  );
}

export default App;
