import React, {useState , useEffect} from 'react'
import ActorCard from './ActorCard'

function AllActors(props){
const [data , setData]= useState("")

const API = `https://kitsu.io/api/edge/anime/${props.num}/anime-staff`

    useEffect(()=>{ 
        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            setData(prev=> ({ ...data.data,...prev.data}))
            }) 
        }
        ,[props.num])

        console.log(data)

return data ? "err" : data.data.map((e)=>{
   return <ActorCard id={e.id}/>
})

}

export default AllActors
