import React, {useState , useEffect} from 'react'
import Slider from 'react-slick'
import ActorCard from './ActorCard'

function AllActors(props){

  const [Data, setData] = useState([{data:[], next:[]}])

const API = `https://kitsu.io/api/edge/anime/${props.num}/staff`

function ReadData(API){

  fetch(API)
  .then((res)=>res.json())
  .then((data)=>{
          setData(prev=> ({ data: data.data,   next: data.links.next ,  }))}) 
}

useEffect(()=>{
     ReadData(API)
        // eslint-disable-next-line react-hooks/exhaustive-deps
  },[props.num])

  function LoadMore(){
      fetch(Data.next)
      .then((res)=>res.json())
      .then((data)=>{
          setData((prev => ({...prev, data: [...prev.data ,...data.data], next:data.links.next}))) 
      })  
  }

    
    var settings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 5,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
              initialSlide: 1
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
      };




return !Data.data ? "errorrrrrrrrrrrr" :
<Slider {...settings}>
{Data.data.map((e)=>{
  return <div  key={e.id}> <ActorCard id={e.id} role={e.attributes.role}/> </div>
})}
<div onClick={LoadMore}>
  <h1 style={{ background:" #2b2b31", padding: "154px 0"}}>LoadMoRE</h1>
</div>
</Slider>
}

export default AllActors
