import { useState } from "react"

    const [Data, setData] = useState([{data:[], next:[]}])


export default function LoadMore(){
   fetch(Data.next)
   .then((res)=>res.json())
   .then((data)=>{
       setData((prev => ({...prev, data: [...prev.data ,...data.data], next:data.links.next}))) 
   })  
}
