import React , {useState , useEffect} from 'react'



function Character(props){

    


    const [Character , SetCharacter] = useState([])
    const SINGEL_CHARACTER_API = ``

    const readCharacter = (API)=>{
        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            SetCharacter(prev=> ({
                data: data.data,
                }))
            }) 
         
    }   

    
    props.array.forEach(e => {
        readCharacter(`https://kitsu.io/api/edge/media-characters/${e}/character`)
    });
    console.log(Character)
/*     useEffect(()=>{
