import React,{useState , useEffect} from 'react'
import Character from './character'

function Characters(props){


    const [Characters , SetCharacters] = useState([{data:[], next:[]}])
    const [ClickedMoreEpi , setClickedMoreEpi ] = useState(false)
    const [LoadingEpisode , setLoadingEpisode ] = useState(false)
    
    
    const CHARACTERS_API = `https://kitsu.io/api/edge/anime/${props.num}/characters`
  
    const readCharacters = (API)=>{
        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            SetCharacters(prev=> ({
                    data: data.data,
                    next: data.links.next ,
                    loaded: true
                    }))
                }) 
    
    }
    
    useEffect(()=>{
        setTimeout(() => {
            readCharacters(CHARACTERS_API)
        }, 1000);
        },[props.num])
        const myArray = []    
    return <div>
     <h1>Characters</h1>
            {!Characters.data ? null :Characters.data.map((e)=>{
       return <div key={e.id}> 
        <Character array={myArray} id={e.id}/>
       {e.id ?   <div> 
 
       </div>   : 'This is a Movie /// missing episode name'}
   </div>
    })}
    </div>
}

export default Characters
