import React,{useState, useEffect} from 'react'
import SingelEpisode from './SingelEpisode'




function SingelAnime(props){


    const [singel , setSingel] = useState([])
    const [isLoading , setIsLoading ]= useState([false])

    const SINGEL_API = `https://kitsu.io/api/edge/anime?filter[id]=`
    const Youtube_link = `https://www.youtube.com/embed/`

    const readSingel = (API)=>{
        setIsLoading(true)
        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            setSingel((prev)=>[...data.data])    
        })
    console.log(props.cardNumber + "Renders")
    }
   
   useEffect(()=>{
   readSingel(SINGEL_API+props.cardNumber)
   setTimeout(() => {
       setIsLoading(false)
   }, 2000);
   },[props.cardNumber])

console.log(singel)
    return(

      !isLoading ?  <div>
      <h1 >{singel[0].attributes.canonicalTitle}</h1>  
      {singel[0].attributes.coverImage == null ?
       <img alt="anime Charcter in japan" src="https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/71b0a868-5bf0-4c2f-ba02-d7b6e81d78e6/d7t1lcx-f63e32f5-57af-431a-8f05-22d3d75a5414.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJ1cm46YXBwOiIsImlzcyI6InVybjphcHA6Iiwib2JqIjpbW3sicGF0aCI6IlwvZlwvNzFiMGE4NjgtNWJmMC00YzJmLWJhMDItZDdiNmU4MWQ3OGU2XC9kN3QxbGN4LWY2M2UzMmY1LTU3YWYtNDMxYS04ZjA1LTIyZDNkNzVhNTQxNC5qcGcifV1dLCJhdWQiOlsidXJuOnNlcnZpY2U6ZmlsZS5kb3dubG9hZCJdfQ.9uc1GVEcMeAefdNRe10bFuWH5p1jomvOgNk3GrKyjUQ"/>  
       : <img alt={singel[0].attributes.canonicalTitle} src={singel[0].attributes.coverImage.small}/> }
      <p>{singel[0].attributes.description}</p>
      {Youtube_link+singel[0].attributes.youtubeVideoId ? <iframe src= {Youtube_link+singel[0].attributes.youtubeVideoId}
              frameBorder='0'
              allow='autoplay; encrypted-media'
              title='video'
      />
       : null}
      
       
      <div></div>
       
<SingelEpisode num={props.cardNumber}></SingelEpisode>
        <div><button onClick={props.showOn}>to Close Click here or Press "ESC"</button></div>
      
       
      </div>  : 'loading'
    
   )
    
}

export default SingelAnime
