import React,{useState, useEffect} from 'react'
import SingelEpisode from './Episodes'
import Character from './characters'
import { useHistory } from 'react-router-dom';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";
import Skeleton from 'react-loading-skeleton'

function SingelAnime(props){


    const [singel , setSingel] = useState([])
    const [isLoading , setIsLoading ]= useState([false])

    const SINGEL_API = `https://kitsu.io/api/edge/anime?filter[id]=`
    const Youtube_link = `https://www.youtube.com/embed/`

    const readSingel = (API)=>{
        setIsLoading(true)
        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            setSingel((prev)=>[...data.data])    
        })
    }
   
   useEffect(()=>{
   readSingel(SINGEL_API+props.cardNumber)
   setTimeout(() => {
       setIsLoading(false)
   }, 1200);
   },[props.cardNumber])
   

   function Characterr() {
    return (
      <div>
      
      <h2>Characters</h2>
       <Character num={props.cardNumber}></Character>
      </div>
    );
  }
  
  function Episode() {
    return (
      <div>
      <h2>Episodes</h2>
        <SingelEpisode num={props.cardNumber}></SingelEpisode>
      </div>
    );
  }
  
  function Actors() {
    return (
      <div>
        <h2>Actors</h2>
      </div>
    );
  }


   const history = useHistory()
console.log(history)
    return(
      !isLoading ?  <div> 
      <h1 >{singel[0].attributes.canonicalTitle}</h1>  

      <div className="Singel-Card">
      {singel[0].attributes.coverImage == null ?
       <div style={{backgroundImage: `url(https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/71b0a868-5bf0-4c2f-ba02-d7b6e81d78e6/d7t1lcx-f63e32f5-57af-431a-8f05-22d3d75a5414.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJ1cm46YXBwOiIsImlzcyI6InVybjphcHA6Iiwib2JqIjpbW3sicGF0aCI6IlwvZlwvNzFiMGE4NjgtNWJmMC00YzJmLWJhMDItZDdiNmU4MWQ3OGU2XC9kN3QxbGN4LWY2M2UzMmY1LTU3YWYtNDMxYS04ZjA1LTIyZDNkNzVhNTQxNC5qcGcifV1dLCJhdWQiOlsidXJuOnNlcnZpY2U6ZmlsZS5kb3dubG9hZCJdfQ.9uc1GVEcMeAefdNRe10bFuWH5p1jomvOgNk3GrKyjUQ")`}}></div>

     :  <div style={{backgroundImage: `url(${singel[0].attributes.coverImage.large})`}}></div>
}
      <p>{singel[0].attributes.description}</p>

      </div>
    
      {Youtube_link+singel[0].attributes.youtubeVideoId ? <iframe 
              src= {Youtube_link+singel[0].attributes.youtubeVideoId}
              frameBorder='0'
              allow='autoplay; encrypted-media'
              title='video'
      />
       : null}
      
       
      <div></div>


      <Router>
      <div c>
        <ul className="router-Grid">
          <li>
            <Link to={`${history.location.pathname}/Episode`}>Episode</Link>
          </li>
          <li>
            <Link to={`${history.location.pathname}/Characters`}>Character</Link>
          </li>
          <li>
            <Link to={`${history.location.pathname}/Actors`}>Actors</Link>
          </li>
        </ul>

        <hr />

        {/*
          A <Switch> looks through all its children <Route>
          elements and renders the first one whose path
          matches the current URL. Use a <Switch> any time
          you have multiple routes, but you want only one
          of them to render at a time
        */}
        <Switch>
          <Route exact path={`${history .location.pathname}/Episode`}>
            <Episode/>
          </Route>
          <Route path={`${history.location.pathname}/Characters`}>
            <Characterr/>
          </Route>
          <Route path={`${history.location.pathname}/Actors`}>
            <Actors/>
          </Route>
        </Switch>
      </div>
    </Router>

      




        <div style={{position:"absolute" , top:"2%" , right:"0%"}}><button onClick={ () => {history.goBack()}}>to Close Click here or Press "ESC"</button></div>
      
       
      </div>  :  <Skeleton /> 
    
   )
    
}

export default SingelAnime
