import React from 'react'
import { useHistory } from 'react-router-dom';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";
import SingelEpisode from './components/Episodes'
import Characters from './components/characters'
import Overview from './components/Overview'
import AllActors from './components/AllActors';
import Slider from 'react-slick';

function RouterSingelAnime(props){


  var settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 5,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          initialSlide: 1
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  };

    function Characterr() {
        return (
          <div>
          
          <h2>Characters</h2>
           <Characters num={props.num}></Characters>
          </div>
        );
      }
      
      function Episode() {
        return (
          <div>
          <h2>Episodes</h2>
            <SingelEpisode num={props.num}></SingelEpisode>
          </div>
        );
      }
      
      function Actors() {
        return (  <Slider settings={settings}>
        <AllActors num={props.num}/>
          </Slider>
          
        )
      }


    
    
