       const history = useHistory()
    return <div>

       
              <Router>
      <div style={{textAlign:"center" , textAlign: "-webkit-center" , background: "#141414"}} >
        <ul className="router-Grid">
        <li>
            <Link to={`/${props.num}`}>Overview</Link>
          </li>
          <li>
            <Link to={`/${props.num}/Episode`}>Episode</Link>
          </li>
          <li>
            <Link to={`/${props.num}/Characters`}>Character</Link>
          </li>
          <li>
            <Link to={`/${props.num}/Actors`}>Actors</Link>
          </li>
        </ul>
        <Switch>
        <Route exact path={`/${props.num}/`}>
            <Overview data={props.mydata}/>
          </Route>
          <Route exact path={`/${props.num}/Episode`}>
            <Episode/>
          </Route>
          <Route path={`/${props.num}/Characters`}>
            <Characterr/>
          </Route>
          <Route path={`/${props.num}/Actors`}>
            <Actors/>
          </Route>
        </Switch>
      </div>
    </Router>
    </div>
}

export default RouterSingelAnime
