import React, { useState, useEffect } from "react";
import Card from "./cards";

const Fetch = () => {
  const [data, setData] = useState({
      data : "",
      links : "",
      meta : ""
  });
  const [isLoading , setIsLoading] = useState(true)
  const [page , setPage] = useState(20)


  const fetchData = useEffect(()=>{
    const url = 'https://kitsu.io/api/edge/anime?page[limit]=20&page[offset]='+{page}
    setTimeout(()=>fetch(url)
      .then(response => response.json())
      .then(json => {        
          setData(json)
        setIsLoading(false)        
      }),1000)
  
  },[])


  useEffect(()=>{
     let dataFetch = async ()=> {
         setIsLoading(false)
         if (!setIsLoading){
         setPage(page+20)
         } 
         const newAnime = await  fetchData(page)
         setData((prev)=>[...prev , ...newAnime ])
         setIsLoading(true)
     }
  })

    

  console.log(data)
  return(
  isLoading ? 'Loading...' : 
  data.data.map(e=>
    <Card id={e.id} name={e.attributes.canonicalTitle} img={e.attributes.posterImage.small} description={e.attributes.description}/>
   
      )
     
  )};

export default Fetch;
