import React from 'react'
import Slider from 'react-slick'

function SingelEpisode(props){

const data = props.data
var settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 4,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          initialSlide: 1
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  };

    return (
        
        <Slider {...settings}>
  {      data.data.map((e)=>{
       return <div key={e.id}> 
        {e.attributes.canonicalTitle ? <div> 
         <h1>Episode {+e.attributes.number}</h1>
         <h4 >{e.attributes.canonicalTitle}</h4>
         <p>{e.attributes.airdate}</p> 
         <p>{e.attributes.description}</p> 
         </div> : 'This is a Movie /// missing episode name'}
    </div>
    })}
    <button onClick={props.onClick}>Click me</button>
    </Slider> )
}

export default SingelEpisode
