import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { useSpring , animated, config } from 'react-spring'
import { Transition } from 'react-spring/renderprops'


import SearchAnime from './SearchAnime'
import HomeSvg from './SVG/HomeSVG'
import MovieSVG from './SVG/MovieSVG'
import SearchSVG from './SVG/SearchSVG'

function NavBar(){

const[show,setShow]=useState(false)

const transitions = useTransition(items, item => item.key, {
    from: { transform: 'translate3d(0,-40px,0)' },
    enter: { transform: 'translate3d(0,0px,0)' },
    leave: { transform: 'translate3d(0,-40px,0)' },
    })



function ShowSearchBar(props){
    console.log(show)   
     return setShow(!show)
}

   return(
       <div > 
      {!show ? null : <animated.div style={transition}><SearchAnime/></animated.div>}
       <div className="navBar" >
    <div className="navBar-item">
    <Link to="/"><HomeSvg></HomeSvg></Link> 
    <Link to="/movies"><MovieSVG/></Link>
    <div onClick={ ShowSearchBar}>
    <SearchSVG/>
    </div>
    </div>  
    </div>
     </div>)
}
export default NavBar
