import React,{useState} from 'react'
import './App.css';
import Movies from './components/AnimeHouse'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useParams
} from "react-router-dom";
import SingelAnime from './components/SingelAnime';
import SingelEpisode from './components/Episodes';

function App() {

 const SingelAnimel= (props)=>{
  let { slug } = useParams();
    return  <SingelAnime cardNumber={slug}/>
 }


 const Episodes= (props)=> {
  let { slug } = useParams();
    return <SingelEpisode num={slug}></SingelEpisode>
  }



  return (
    <div className="dis">


<div className="grid-container">
<Router>
      <Route path="/" component={Movies}/>
    </Router>
    <Router>
      <Route path="/:slug" component={SingelAnimel}/>
    </Router>
    <Router>
      <Route path="/:slug/episode" component={Episodes}/>
    </Router>
</div>

    </div>

  );
}

export default App;
