import React,{useState, useEffect} from 'react'
import { SkeletonTheme } from 'react-loading-skeleton';
import { useHistory } from 'react-router-dom';
import SingelCardDetails from './SingelCardDeatils';


function SingelAnime(props){

    const [Singel , setSingel] = useState([])
    const SINGEL_API = `https://kitsu.io/api/edge/anime?filter[id]=`

    const readSingel = (API)=>{
        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            setSingel((prev)=>[...data.data])    
        })
    }
   
   useEffect(()=>{
   readSingel(SINGEL_API+props.cardNumber)

   },[props.cardNumber])  

   const history=useHistory()

    //close singel if ESC got clicked

useEffect(() => {
    const close = (e) => {
      if(e.keyCode === 27 && history.location.pathname !== "/"){
        history.goBack()
      }
    }
    window.addEventListener('keydown', close)
  return () => window.removeEventListener('keydown', close)
},[])

    return(
      <div  style={{background:"black"}}>
  {!Singel ? <SkeletonTheme><SingelCardDetails/></SkeletonTheme> :  <SingelCardDetails data={Singel}/>} 
      </div>
   )
    
}

export default SingelAnime
