import React,{useState, useEffect} from 'react'
import Card from './cards'


function Movies(){

const [data , setData] = useState([])
const [isLoading , setIsLoading] = useState(true)
const [value , SetValue] = useState('')
const [isBottom, setIsBottom] = useState(false);

const style={
    fontSize: "2rem", 
    textAlign: "center" ,
    borderRadius:"25px",
    padding : "0% 20%" ,
    marginLeft:"19%"
}


const LISTED_API= `https://kitsu.io/api/edge/anime?page[limit]=20&page[offset]=$`
const SEARCH_API = `https://kitsu.io/api/edge/anime?filter[text]=$`




useEffect(()=>{
        readData(LISTED_API)    
    
},[])


const readData = (API)=> {
    fetch(API)
     .then((res)=>res.json())
     .then((data)=>{
         setData(data.data)
        })
     .then(setIsLoading(false))
 }

 
function handelChange(e){
    const valuer = e.target.value
        SetValue(valuer)
        readData(SEARCH_API+valuer)

        if (!valuer){
            readData(LISTED_API)
        }

    
}

useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

function handleScroll(){
    const scrollTop = (document.documentElement
        && document.documentElement.scrollTop)
        || document.body.scrollTop;
      const scrollHeight = (document.documentElement
        && document.documentElement.scrollHeight)
        || document.body.scrollHeight;
      if (scrollTop + window.innerHeight + 50 >= scrollHeight){
        setIsBottom(true);
      }

console.log(isBottom)



}
    

return(<div>

<header>

   <input style={style} type="text" name="name" placeholder="Search..." onChange={(e)=>{handelChange(e)}} />
   
   <h1>{value}</h1>

</header>

<div className="grid-container">

{isLoading ? 'Loading...' : data.map(e=>
      <Card 
      key={e.id}
     id={e.id}
    name={e.attributes.canonicalTitle}
    img={e.attributes.posterImage.small}
    description={e.attributes.description}/>     
       
    )}

</div>


</div>)
}
    export default Movies
