import React ,{ useState , useEffect} from 'react'
import Card from './cards'

import SearchAnime from './SearchAnime'


function Next(){


    const [data , setData] = useState({
        Loading : false,
        isBottom:false,
        page:0,
        data : []
    })


    const LISTED_API= `https://kitsu.io/api/edge/anime?page[limit]=10&page[offset]=${data.page}`


    useEffect(() => {
       
            readData(LISTED_API)
            
        }
      , []); 

    const readData = (API)=>{
        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            console.log(data)
            setData((prev)=>[...prev])    
        })
    
       
    }
    console.log(data)


    return(
        <div>
        <SearchAnime/>
        <div className="grid-container">

{!data.Loading ? 'loading' : data.map(e=>
      <Card 
      key={e.id}
     id={e.id}
    name={e.attributes.canonicalTitle}
    img={e.attributes.posterImage.small}
    description={e.attributes.description}

    />)}

</div>
        </div>
    )
}

export default Next
