import React, { useState, useEffect } from "react";
import Card from "./cards";
import {getData} from "./newfile"
function Fetch(props) {
  const [data, setData] = useState([]);
  const [isLoading , setIsLoading] = useState(true)
  const [page , setPage] = useState(0)

function HandelScroll(e){

   let alfa =  document.getElementsByClassName("grid-container")

    console.log(alfa)
    console.log(alfa[0].scrollHeight + ":ScrollHeight") 
    console.log(alfa[0].offsetTop + ":ScrollTop") 
    console.log(alfa[0].clientHeight + ":clientHeight") 

}

document.addEventListener("scroll", HandelScroll)

  useEffect(()=>{
     const dataFetch = async ()=> {
         setIsLoading(true)
         const newAnime = await  getData(page)
         setData((prev)=>[...prev , ...newAnime ])
         setIsLoading(false)
     }
     dataFetch()
  },[page])

    console.log(data)
  return(
  isLoading ? 'Loading...' : 
  data.map(e=>
    <div key={e.id}>
  <Card 
    onScroll={HandelScroll(e)}
    id={e.id}
    name={e.attributes.canonicalTitle}
    img={e.attributes.posterImage.small} 
    description={e.attributes.description}/>
    </div>
  
   
      )
     
  )};

export default Fetch;
