import React, { useState, useEffect } from "react";
import Card from "./cards";
import {getData} from "./newfile"
function Fetch(props) {
  const [data, setData] = useState([]);
  const [isLoading , setIsLoading] = useState(true)
  const [page , setPage] = useState(0)

function HandelScroll(e){
    console.log(e.currentTarget)
    console.log(e)
}



  useEffect(()=>{
     const dataFetch = async ()=> {
         setIsLoading(true)
        
         const newAnime = await  getData(page)
         setData((prev)=>[...prev , ...newAnime ])
         setIsLoading(false)
        
     }
     dataFetch()
  },[])
  setPage(page+20)
    console.log(data)
  return(
  isLoading ? 'Loading...' : 
  data.map(e=>
    <Card key={e.id} id={e.id} name={e.attributes.canonicalTitle} img={e.attributes.posterImage.small} description={e.attributes.description}/>
   
      )
     
  )};

export default Fetch;
