import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { useTransition } from 'react-spring'
import { animated } from 'react-spring/renderprops-universal'
import SearchAnime from './SearchAnime'
import HomeSvg from './SVG/HomeSVG'
import MovieSVG from './SVG/MovieSVG'
import SearchSVG from './SVG/SearchSVG'

function NavBar(props){

const[show,setShow]=useState(false)



const transitions = useTransition(show, item => item.key, {
from: { transform: 'translate3d(0,-40px,0)' },
enter: { transform: 'translate3d(0,0px,0)' },
leave: { transform: 'translate3d(0,-40px,0)' },
})


function ShowSearchBar(props){
    console.log(show)   
     return setShow(!show)
}

   return(
       <div > 
      {!show ? <h1>none</h1> : <div>
      { transitions.map(({ item, props, key }) =>
<animated.div key={key} style={props}>{item.text}</animated.div>
) }
       </div>
       
        }
       <div className="navBar" >
    <div className="navBar-item">
    <Link to="/"><HomeSvg></HomeSvg></Link> 
    <Link to="/movies"><MovieSVG/></Link>
    <div onClick={ ShowSearchBar}>
    <SearchSVG/>
    </div>
    </div>  
    </div>
     </div>)
}
export default NavBar
